import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import StockList from './components/StockList';
import StockChart from './components/StockChart';
import SearchBar from './components/SearchBar';

// Use relative URL paths instead of absolute URLs to work with proxy
// This will be automatically forwarded to http://localhost:5000
// const API_URL = 'http://localhost:5000';

function App() {
  const [stockData, setStockData] = useState({});
  const [searchQuery, setSearchQuery] = useState('AAPL');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockData = async () => {
      if (!searchQuery) return;

      setLoading(true);
      setError(null);

      try {
        // Use relative URL path with the proxy setup
        const response = await axios.get(
          `/api/stock?symbol=${searchQuery}`
        );

        if (response.data && !response.data.error) {
          setStockData(response.data);
        } else {
          setError(response.data.error || 'No data found for this stock symbol');
        }
      } catch (err) {
        setError('Error fetching stock data. Please try again.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchStockData();
  }, [searchQuery]);

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Stock Market App (Yahoo Finance)</h1>
      </header>
      <main>
        <SearchBar onSearch={handleSearch} />

        {loading && <p className="loading">Loading stock data...</p>}
        {error && <p className="error">{error}</p>}

        {!loading && !error && Object.keys(stockData).length > 0 && (
          <div className="stock-container">
            <StockList stockData={stockData} />
            <StockChart stockData={stockData} />
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
