import React from 'react';

const TechnicalAnalysis = ({ stockData }) => {
  // Calculate simple moving averages (would be from historical data in a real app)
  const calculateRecommendation = (stockData) => {
    const price = parseFloat(stockData['05. price'] || 0);
    const open = parseFloat(stockData['02. open'] || 0);
    const high = parseFloat(stockData['03. high'] || 0);
    const low = parseFloat(stockData['04. low'] || 0);
    const prevClose = parseFloat(stockData['08. previous close'] || 0);
    const change = parseFloat(stockData['09. change'] || 0);

    // Very simple technical analysis indicators
    const isPriceAbovePrevClose = price > prevClose;
    const isStrongOpen = open > prevClose;
    const volatility = (high - low) / low * 100;
    const momentum = Math.abs(change) / prevClose * 100;

    let score = 0;
    let signals = [];

    // Price trend
    if (isPriceAbovePrevClose) {
      score += 1;
      signals.push("Price is above previous close (bullish)");
    } else {
      score -= 1;
      signals.push("Price is below previous close (bearish)");
    }

    // Strong open
    if (isStrongOpen) {
      score += 1;
      signals.push("Strong market open (bullish)");
    } else {
      score -= 0.5;
      signals.push("Weak market open (bearish)");
    }

    // Volatility
    if (volatility > 2) {
      signals.push(`High volatility (${volatility.toFixed(2)}%)`);
      // High volatility is neutral for recommendation
    } else {
      signals.push(`Low volatility (${volatility.toFixed(2)}%)`);
    }

    // Momentum
    if (momentum > 1) {
      if (isPriceAbovePrevClose) {
        score += 1;
        signals.push(`Strong positive momentum (${momentum.toFixed(2)}%)`);
      } else {
        score -= 1;
        signals.push(`Strong negative momentum (${momentum.toFixed(2)}%)`);
      }
    }

    // Determine recommendation
    let recommendation = "Hold";
    let color = "#f0ad4e"; // Yellow/orange for hold

    if (score >= 2) {
      recommendation = "Strong Buy";
      color = "#5cb85c"; // Green
    } else if (score > 0) {
      recommendation = "Buy";
      color = "#5bc0de"; // Light blue
    } else if (score < -1) {
      recommendation = "Strong Sell";
      color = "#d9534f"; // Red
    } else if (score < 0) {
      recommendation = "Sell";
      color = "#f0605e"; // Light red
    }

    return { recommendation, signals, color };
  };

  const { recommendation, signals, color } = calculateRecommendation(stockData);

  return (
    <div className="card">
      <h2>Technical Analysis</h2>

      <div className="recommendation-container">
        <div 
          className="recommendation" 
          style={{ 
            backgroundColor: color,
            padding: '15px',
            borderRadius: '5px',
            color: 'white',
            fontWeight: 'bold',
            textAlign: 'center',
            fontSize: '1.2rem',
            marginBottom: '15px'
          }}
        >
          {recommendation}
        </div>

        <h3>Signals:</h3>
        <ul className="signals-list">
          {signals.map((signal, index) => (
            <li key={index}>{signal}</li>
          ))}
        </ul>

        <div className="disclaimer">
          <small>
            <em>Disclaimer: This is a simplified analysis for demonstration purposes only. 
            Do not make investment decisions based solely on this information.</em>
          </small>
        </div>
      </div>
    </div>
  );
};

export default TechnicalAnalysis;
