import React, { useState, useEffect } from 'react';
import { 
  BarChart, Bar, LineChart, Line, ComposedChart, 
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, 
  ResponsiveContainer, PieChart, Pie, Cell
} from 'recharts';

const StockChart = ({ stockData }) => {
  const [chartType, setChartType] = useState('bar');
  const [historicalData, setHistoricalData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch historical data from backend API
  useEffect(() => {
    if (stockData && stockData['01. symbol']) {
      setIsLoading(true);
      setError(null);

      const symbol = stockData['01. symbol'];
      const period = '1mo'; // Default to 1 month
      const interval = '1d'; // Default to daily data

      fetch(`http://localhost:5001/api/stock/history?symbol=${symbol}&period=${period}&interval=${interval}`)
        .then(response => {
          if (!response.ok) {
            throw new Error('Failed to fetch historical data');
          }
          return response.json();
        })
        .then(data => {
          if (data && data.data && Array.isArray(data.data)) {
            setHistoricalData(data.data);
          } else {
            setHistoricalData([]);
            setError('Invalid data format received');
          }
          setIsLoading(false);
        })
        .catch(err => {
          console.error('Error fetching historical data:', err);
          setError(err.message);
          setIsLoading(false);
          // Fallback to generated data if API fails
          setHistoricalData(generateHistoricalData());
        });
    }
  }, [stockData]);

  // Creating data for current day price levels
  const prepareChartData = () => {
    if (!stockData || !stockData['05. price']) {
      return [];
    }

    const basePrice = parseFloat(stockData['05. price']);
    const change = parseFloat(stockData['09. change'] || 0);

    // Create daily price data for visualization
    return [
      { name: 'Open', price: parseFloat(stockData['02. open'] || basePrice) },
      { name: 'Low', price: parseFloat(stockData['04. low'] || (basePrice - Math.abs(change))) },
      { name: 'Current', price: basePrice },
      { name: 'High', price: parseFloat(stockData['03. high'] || (basePrice + Math.abs(change))) },
    ];
  };

  // Generate mock historical data as fallback
  const generateHistoricalData = () => {
    if (!stockData || !stockData['05. price']) {
      return [];
    }

    const currentPrice = parseFloat(stockData['05. price']);
    const volatility = 0.02; // 2% daily volatility for simulation
    const data = [];

    // Start with a price 10% lower than current as baseline
    let previousPrice = currentPrice * 0.9;

    // Generate 30 days of data with actual dates
    const today = new Date();

    for (let i = 30; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(today.getDate() - i);

      // Format date as MM/DD
      const formattedDate = `${date.getMonth() + 1}/${date.getDate()}`;

      // Random price movement with slight upward bias
      const change = previousPrice * volatility * (Math.random() - 0.45);
      const price = previousPrice + change;
      previousPrice = price;

      // Generate random volume between 100,000 and 10,000,000
      const volume = Math.floor(Math.random() * 9900000) + 100000;

      data.push({
        date: formattedDate,
        price: parseFloat(price.toFixed(2)),
        volume: volume
      });
    }

    return data;
  };

  // Generate data for price movement composition (pie chart)
  const generatePriceCompositionData = () => {
    if (!stockData) return [];

    const price = parseFloat(stockData['05. price'] || 0);
    const open = parseFloat(stockData['02. open'] || 0);
    const change = price - open;

    // Components that make up the current price (simplified for demonstration)
    return [
      { name: 'Base Value', value: open > 0 ? open : price * 0.9 },
      { name: 'Market Movement', value: Math.abs(change) },
      { name: 'Volatility', value: price * 0.01 }
    ];
  };

  // Generate data for price comparison with sector and index
  const generateComparisonData = () => {
    if (!stockData || !stockData['05. price']) {
      return [];
    }

    const stockPrice = parseFloat(stockData['05. price']);
    const stockChange = parseFloat(stockData['09. change'] || 0);
    const stockPercentChange = (stockChange / stockPrice) * 100;

    // Mock data for sector and market indices performance
    const sectorChange = (Math.random() * 2 - 1) * 2; // Between -2% and 2%
    const marketChange = (Math.random() * 1.5 - 0.5) * 1.5; // Between -1.5% and 1.5%

    return [
      {
        name: stockData['01. symbol'],
        performance: parseFloat(stockPercentChange.toFixed(2)),
        fill: stockPercentChange >= 0 ? '#4CAF50' : '#F44336'
      },
      {
        name: 'Sector Avg',
        performance: parseFloat(sectorChange.toFixed(2)),
        fill: sectorChange >= 0 ? '#4CAF50' : '#F44336'
      },
      {
        name: 'Market Index',
        performance: parseFloat(marketChange.toFixed(2)),
        fill: marketChange >= 0 ? '#4CAF50' : '#F44336'
      }
    ];
  };

  const chartData = prepareChartData();
  const compositionData = generatePriceCompositionData();
  const comparisonData = generateComparisonData();

  // Determine if stock is up or down for color schemes
  const isStockUp = parseFloat(stockData['09. change'] || 0) >= 0;
  const mainColor = isStockUp ? '#4CAF50' : '#F44336';
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28'];

  // Chart type options
  const chartTypes = [
    { id: 'bar', label: 'Price Levels' },
    { id: 'line', label: 'Price History' },
    { id: 'volume', label: 'Volume Analysis' },
    { id: 'comparison', label: 'Performance Comparison' },
    { id: 'pie', label: 'Price Composition' },
  ];

  // Loading state
  if (chartType === 'line' && isLoading) {
    return (
      <div className="chart-container" style={{ height: '400px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <p>Loading historical data...</p>
      </div>
    );
  }

  // Error state
  if (chartType === 'line' && error && historicalData.length === 0) {
    return (
      <div className="chart-container" style={{ height: '400px', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <p>Error loading data: {error}</p>
      </div>
    );
  }

  // Render different chart based on selection
  const renderSelectedChart = () => {
    switch(chartType) {
      case 'bar':
        return (
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={chartData}
              margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis 
                domain={[
                  (dataMin) => Math.floor(dataMin * 0.995),
                  (dataMax) => Math.ceil(dataMax * 1.005)
                ]}
                tickFormatter={(value) => `$${value.toFixed(2)}`}
              />
              <Tooltip 
                formatter={(value) => [`$${value.toFixed(2)}`, 'Price']}
              />
              <Legend />
              <Bar dataKey="price" fill={mainColor} />
            </BarChart>
          </ResponsiveContainer>
        );

      case 'line':
        return (
          <ResponsiveContainer width="100%" height="100%">
            <LineChart
              data={historicalData}
              margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis 
                domain={['auto', 'auto']}
                tickFormatter={(value) => `$${value.toFixed(2)}`}
              />
              <Tooltip 
                formatter={(value) => [`$${value.toFixed(2)}`, 'Price']} 
                labelFormatter={(label) => `Date: ${label}`}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="price" 
                stroke={mainColor} 
                strokeWidth={2}
                dot={{ r: 0 }}
                activeDot={{ r: 6 }}
              />
            </LineChart>
          </ResponsiveContainer>
        );

      case 'volume':
        return (
          <ResponsiveContainer width="100%" height="100%">
            <ComposedChart
              data={historicalData}
              margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="date" />
              <YAxis 
                yAxisId="left"
                orientation="left"
                tickFormatter={(value) => `$${value.toFixed(2)}`}
              />
              <YAxis 
                yAxisId="right"
                orientation="right"
                domain={['auto', 'auto']}
                tickFormatter={(value) => `${(value/1000000).toFixed(1)}M`}
              />
              <Tooltip 
                formatter={(value, name) => {
                  return name === 'price' 
                    ? [`$${value.toFixed(2)}`, 'Price'] 
                    : [`${(value/1000000).toFixed(1)}M`, 'Volume'];
                }}
              />
              <Legend />
              <Line 
                yAxisId="left"
                type="monotone" 
                dataKey="price" 
                stroke={mainColor} 
                strokeWidth={2}
                dot={false}
              />
              <Bar 
                yAxisId="right"
                dataKey="volume" 
                fill="#8884d8" 
                opacity={0.5} 
              />
            </ComposedChart>
          </ResponsiveContainer>
        );

      case 'comparison':
        return (
          <ResponsiveContainer width="100%" height="100%">
            <BarChart
              data={comparisonData}
              margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis 
                tickFormatter={(value) => `${value}%`}
                domain={[
                  dataMin => Math.min(dataMin, -2),
                  dataMax => Math.max(dataMax, 2)
                ]}
              />
              <Tooltip 
                formatter={(value) => [`${value}%`, 'Performance']}
              />
              <Legend />
              <Bar dataKey="performance" fill="#8884d8">
                {
                  comparisonData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.fill} />
                  ))
                }
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        );

      case 'pie':
        return (
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={compositionData}
                cx="50%"
                cy="50%"
                labelLine={true}
                label={({name, percent}) => `${name}: ${(percent * 100).toFixed(0)}%`}
                outerRadius={100}
                fill="#8884d8"
                dataKey="value"
              >
                {
                  compositionData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))
                }
              </Pie>
              <Tooltip 
                formatter={(value) => [`$${value.toFixed(2)}`, 'Amount']}
              />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        );

      default:
        return (
          <div>Please select a chart type</div>
        );
    }
  };

  return (
    <div className="stock-chart">
      <div className="chart-selectors">
        {chartTypes.map((type) => (
          <button
            key={type.id}
            className={`chart-type-btn ${chartType === type.id ? 'active' : ''}`}
            onClick={() => setChartType(type.id)}
          >
            {type.label}
          </button>
        ))}
      </div>
      <div className="chart-container" style={{ height: '400px' }}>
        {!stockData || Object.keys(stockData).length === 0 ? (
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
            <p>Please select a stock to display chart</p>
          </div>
        ) : (
          renderSelectedChart()
        )}
      </div>
    </div>
  );
};

export default StockChart;
